    <div class="container">
        <header>
            <h1><i class="fas fa-heart"></i> Serenity Mood Tracker</h1>
            <p>Track your emotions, discover patterns, and improve your mental well-being</p>
        </header>

        <div class="app-container">
            <div class="card">
                <h2><i class="fas fa-plus-circle"></i> Log Your Mood</h2>
                <div class="mood-selector">
                    <div class="mood-option" data-mood="1">
                        <i class="fas fa-sad-tear"></i>
                        <div class="mood-label">Very Sad</div>
                    </div>
                    <div class="mood-option" data-mood="2">
                        <i class="fas fa-frown"></i>
                        <div class="mood-label">Sad</div>
                    </div>
                    <div class="mood-option" data-mood="3">
                        <i class="fas fa-meh"></i>
                        <div class="mood-label">Neutral</div>
                    </div>
                    <div class="mood-option" data-mood="4">
                        <i class="fas fa-smile"></i>
                        <div class="mood-label">Happy</div>
                    </div>
                    <div class="mood-option" data-mood="5">
                        <i class="fas fa-laugh-beam"></i>
                        <div class="mood-label">Very Happy</div>
                    </div>
                </div>

                <div class="notes-section">
                    <h3><i class="fas fa-sticky-note"></i> Notes (Optional)</h3>
                    <textarea id="mood-notes" placeholder="What made you feel this way today?"></textarea>
                </div>

                <button id="log-mood-btn" class="btn">
                    <i class="fas fa-save"></i> Save Mood Entry
                </button>

                <div class="stats">
                    <div class="stat-item">
                        <div class="stat-value" id="total-entries">0</div>
